import './CSS/Boards.css'; 
import './CSS/Footer.css'; 
import { useEffect, useState } from "react";
import { post_Requests } from './Utilities/Request';

export function Tasks(props){

    const [tasks, settasks] = useState([]);
    const [filteredMessage, setFilteredtasks] = useState([]); // for filtere tasks
    const [focused, setFocused] = useState(null); // changed task

    useEffect(() => {
        const websocket = new WebSocket(`ws://localhost:8000/ws/boards/${props.id}`);

        websocket.onopen = () => {
            websocket.send("get me all tesks"); // send a new request to server to see all tasks in a board
        };

        websocket.onmessage = (event) => {
            const data = JSON.parse(event.data); // create object from strigified message from server
            if(Object.keys(data).includes('data')){ // added/modified task outstanding
                setFocused({id : data['data']['id'] , action : data['action']}); 
                settasks(prev => [...[data['data']], ...prev]);
            }
            else{
                settasks(prev => [...prev, ...data]); // load all the tasks
            }
        };

        websocket.onclose = () => {

        };

        return () => websocket.close();
    }, []);


    //____________filter tasks___________________

    const filter = (e)=>{
        const filtered = tasks.filter(task =>
            task.description.toLowerCase().includes(e.target.value.toLowerCase())
        );

        alert(JSON.stringify(filtered))
    }

    //__________Add new Task To Board_______________

    const Add_Task = async(e)=>{
        e.preventDefault();
        let data = Object.fromEntries(new FormData(e.target).entries());
        post_Requests("api/Boards/Tasks/Add" , data, props.token); // add new task to specific board
    }   

    return(<>

        <div className='container-fluid' id='Tasks'>
            <div className='row'>
                <div className='col-xs-12 col-sm-12 col-md-6 col-lg-9'>
                    <h4 style={{paddingLeft:"20px", paddingTop:"10px"}}>
                        All Tasks of:  
                        <span style={{paddingLeft:"10px", color:"carbonblue"}}>
                            {
                                props.title
                            }
                        </span>
                        <div>
                            <input style={{width:"98%"}} placeholder = "Filter By Task,s Description" type="text" id="description" className='boards_input' onChange={filter}/>
                        </div>
                    </h4>
                    <div class="boards_container">
                        {
                            tasks !== null &&
                            tasks.map((x,index)=> //).map((xx , index) => 
                                <div key={index} class="boards_card" onClick={()=>{}} style={{border: focused !== null && focused.id === x.id ? "solid 2px #A8DCAB" : "solid 2px silver"}}>
                                    <div style={{paddingBottom:"10px"}}>
                                        <h2 style={{paddingTop:"10px", paddingBottom:"10px", borderBottom:"solid thin silver", marginBottom:"10px"}}> Status: <span style={{color:"orangered"}}>{x.status}</span></h2>
                                        <div class="boards_count"> Created at: {x.created_at.replace('T' ,' ')}</div>
                                        <div class="boards_creator">Created by: {x.cretaed_by_userName}</div>
                                        <div class="boards_count"> Updated at: {x.created_at.replace('T' ,' ')}</div>
                                        <div class="boards_creator">Updated by: {x.updated_by_userName}</div>
                                    </div>
                                    <div style={{paddingTop:"10px", padding:"5px", backgroundColor:"#d7d7d8", borderRadius:"5px", color:"navy"}}>
                                        {
                                            x.description
                                        }
                                    </div>
                                </div>
                            )
                        }

                    </div>
                </div>
                <div className='col-xs-12 col-sm-12 col-md-6 col-lg-3' style={{paddingTop:"20px"}}>
                    <div className='bards_form' >
                        <h2>Add New Task</h2>
                        <form onSubmit={Add_Task}>

                            <input type='hidden' name='board_id' value={props.id} />
                            <input type='hidden' name='cretaed_by' value={props.u_id} />
                            <input type='hidden' name='cretaed_by_userName' value={props.username} />
                            <input type='hidden' name='updated_by_userName' value={props.username} />
                            <input type='hidden' name='status' value="in progress" />

                            <label for="title">Description</label><br />
                            <input type="text" id="description" name="description" className='boards_input' autoComplete='off' required/>

                            <button type="submit" className='button_style'>Create</button>
                        </form>
                    </div>
                </div>
            </div>

            <div style={{padding:"20px", height:"50px"}}>

            </div>

            <div className='footer'>
                <div class="username">{props.id}</div>
                <button class="logout-btn" onClick={props.close_handler}>Close &times;</button>
            </div>

        </div>

    </>)
}
