import './CSS/Boards.css'; 
import './CSS/Footer.css'; 
import { useEffect, useState } from "react";
import { post_Requests } from './Utilities/Request';

export function Tasks(props){

    const [messages, setMessages] = useState([]);

    useEffect(() => {
        const websocket = new WebSocket(`ws://localhost:8000/ws/boards/${props.id}`);

        websocket.onmessage = (event) => {
            const data = JSON.parse(event.data); // create object from strigified message from server
            setMessages(prev => [...prev, data]);
        };

        websocket.onclose = () => {

        };

        return () => websocket.close();
    }, []);

    //__________Add new Task To Board_______________

    const Add_Task = async(e)=>{
        e.preventDefault();
        let data = Object.fromEntries(new FormData(e.target).entries());
        post_Requests("api/Boards/Tasks/Add" , data, props.token); // add new task to specific board
    }   

    return(<>

        <div className='container-fluid' id='Tasks'>
            <div className='row'>
                <div className='col-xs-12 col-sm-12 col-md-6 col-lg-9'>
                    <h4 style={{paddingLeft:"20px", paddingTop:"10px"}}>
                        {
                            props.title
                        }
                    </h4>
                    <div class="boards_container">
                        {
                            messages !== null &&
                            messages.map((x,index)=>
                            <div key={index} class="boards_card" onClick={()=>{}}>
                            <h2> Created By: <span style={{color:"orangered"}}>{x.title}</span></h2>
                                <div class="boards_count"> Created at: {x.creation_date.split('T')[0]}</div>
                                <div class="boards_creator">Created by: {x.created_by}</div>
                            </div>
                            )
                        }

                    </div>
                </div>
                <div className='col-xs-12 col-sm-12 col-md-6 col-lg-3' style={{paddingTop:"20px"}}>
                    <div className='bards_form'>
                        <h2>Add New Task</h2>
                        <form onSubmit={Add_Task}>

                            <input type='hidden' name='board_id' value={props.id} />
                            <input type='hidden' name='cretaed_by' value={props.u_id} />
                            <input type='hidden' name='cretaed_by_userName' value={props.username} />
                            <input type='hidden' name='status' value="in_progress" />

                            <label for="title">Description</label><br />
                            <input type="text" id="description" name="description" className='boards_input' autoComplete='off' required/>

                            <button type="submit" className='button_style'>Create</button>
                        </form>
                    </div>
                </div>
            </div>

            <div className='footer'>
                <div class="username">{props.id}</div>
                <button class="logout-btn" onClick={props.close_handler}>Close</button>
            </div>

        </div>

    </>)
}
