import Cookie from 'js-cookie';
import { useState, useEffect } from 'react';


export function Panel(){

    //______Redirect Users to Authentication page if token is not valid______

    const token_manager = async ()=> {
        let token = Cookie.get("token"); // does token exist?
        token = token === undefined ? window.location.href = "/" : JSON.parse(token); // convert to Json object

        //____________________Pass token to server_________________________

        let server_response = await check_token_on_server(token.token , token.id); // check token validity (avoid the external unexpected tokens) on server

