import './CSS/Boards.css'; 
import { useEffect, useState } from "react";

export function Tasks(props){

    const newTask = {
        description: "This is a test task",
        status: false,
    };

    const [messages, setMessages] = useState([]);

    useEffect(() => {
        const websocket = new WebSocket(`ws://localhost:8000/ws/boards/${props.board_id}`);

        websocket.onmessage = (event) => {
            const data = JSON.parse(event.data); // create object from strigified message from server
            setMessages(prev => [...prev, data]);
        };

        websocket.onclose = () => {

        };

        return () => websocket.close();
    }, []);

    //__________Add new Task To Board_______________

    const Add_Task = async()=>{
        let response = await fetch('http://localhost:8000/Tasks/Add/1' , {method : "post" , headers : {"content-type" : "application/json"} , body : JSON.stringify(newTask)});
        let result = await response.json();

        alert(JSON.stringify(result))
    }   

    return(<>

        <div className='container-fluid' style={{position:"fixed" , top:"0px", left:"0px", width:"100%" , height:"100%"}}>
            <div className='row'>
                <div className='col-xs-12 col-sm-12 col-md-6 col-lg-9'>
                    <h4 style={{paddingLeft:"20px", paddingTop:"10px"}}>
                        {
                            props.board_title
                        }
                    </h4>
                    <div class="boards_container">
                        {
                            messages !== null &&
                            JSON.stringify(messages)
                        }

                    </div>
                </div>
                <div className='col-xs-12 col-sm-12 col-md-6 col-lg-3' style={{paddingTop:"20px"}}>
                    <div className='bards_form'>
                        <h2>Add New Task</h2>
                        <form onSubmit={Add_Task}>
                            <label for="title">Board Title</label><br />
                            <input ref={title_ref} type="text" id="title" name="title" className='boards_input' autoComplete='off' />
                            <button type="submit" className='button_style'>Create</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>

    </>)
}
